#include <iostream>
#include <string>
#include <fstream>
using namespace std;

class Solution {
public:
    int numDistinct(string t, string s) {
        if(s.length() == t.length()){
            for (int i = 0; i < s.length(); i++) {
                if(s[i] != t[i]) return 0;
            }
            return 1;
        }
        if(s.length() > t.length()){
            return 0;
        }
        signed long int ansMatrix[t.length()];
        int curNum = 0;
        for (int i = 0; i < t.length(); i++){
            if(s[0] == t[i]) curNum += 1;
            ansMatrix[i] = curNum;
        }
        for (int i = 1; i < s.length(); i++){
            for (int j = 0; j < t.length(); j++){
                int prevAns;
                if(j < i){
                    prevAns = ansMatrix[j];
                    ansMatrix[j] = 0;
                }
                else{
                    int tempPrevAns = ansMatrix[j];
                    if(s[i] == t[j]) ansMatrix[j] = ansMatrix[j-1] + prevAns;
                    else ansMatrix[j] = ansMatrix[j-1];
                    prevAns = tempPrevAns;
                }
            }
        }
        return ansMatrix[t.length()-1];
    }

};

int main(){
    Solution sol;
    cout  << "sol: " << sol.numDistinct("rabbbit", "rabbit") << endl;
    cout  << "sol: " << sol.numDistinct("babgbag", "bag") << endl;
    cout  << "sol: " << sol.numDistinct("ffff", "fff") << endl;
    cout  << "sol: " << sol.numDistinct("", "rwmimatmhydhbujebqehjprrwfkoebcxxqfktayaaeheys") << endl;
    cout  << "sol: " << sol.numDistinct("", "") << endl;
    cout  << "sol: " << sol.numDistinct("aacaacca", "ca") << endl;
    return 0;
}